mapper.select:
select mnum from member
where
mid = #{mid}


		SELECT * FROM (
              SELECT ROWNUM AS RNUM, A.* FROM (
                 SELECT * FROM gympage WHERE gympage.mnum = #{mnum}
             ) A WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
         ) WHERE RNUM > (#{criteria.pageNum}-1) * #{criteria.amount}
